    private void cbFocusGained(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_cbFocusGained
cb.removeAllItems();
        String sql="select empno from payroll1;";
        int a=cb.getSelectedIndex();
        DefaultComboBoxModel b=(DefaultComboBoxModel)cb.getModel();
        try{
            Class.forName("java.sql.Driver");
            Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/payroll","root","info");
            Statement s=con.createStatement();
            ResultSet rs=s.executeQuery(sql);
            while(rs.next()==true) {
                String n=rs.getString("empno");
                b.addElement(n);
                cb.setModel(b); 
            }
            rs.close();
            s.close();
            con.close();
        } catch(Exception e) {
            JOptionPane.showMessageDialog(this, e.getMessage());
        }
    }//GEN-LAST:event_cbFocusGained

