    private void cbItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cbItemStateChanged
String a1=(String)cb.getSelectedItem();
    int a=Integer.parseInt(a1);
    String sql="select name from payroll1 where empno="+(a)+";" ;
try
{
    Class.forName("java.sql.Driver");
    Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/payroll","root","info");
    Statement s=con.createStatement();
    ResultSet rs=s.executeQuery(sql);
    
    while(rs.next()==true)
    {
        String name=rs.getString(1);
        t1.setText(name);
      
    }
    rs.close();
    s.close();
    con.close();
    }
    catch(Exception e)
{JOptionPane.showMessageDialog(this,e.getMessage());
    }
    }//GEN-LAST:event_cbItemStateChanged

